import React from 'react';
import './step.css'

const Step = (winner, {xIsNext}) => {

    let winnerStep = winner;
    console.log(winnerStep)
    let stepXIsNext = xIsNext;
    console.log(stepXIsNext)

    return (
        <p className='game__info'>
        {
             winnerStep ? 'Победил ' + winnerStep : 'Сейчас ходит ' + ( stepXIsNext ? 'X' : 'O')
        }
         {console.log(stepXIsNext)}
    </p>
    );
}

export default Step;
