import React from 'react';
import './step.css'

const Step = (winner, xIsNext) => {
    return (
        <p className='game__info'>
        {
             winner ? 'Победил ' + winner : 'Сейчас ходит ' + ( xIsNext ? 'X' : 'O')
        }
    </p>
    );
}

export default Step;
